                        "Accept: application/json",
                        "Content-Type: application/json",
                        "X-API-Key: ".$this->getSettings(),
                        "X-Auth-Type: API-Key"
                      ],
                    ]);
                    
                    $response = json_decode(curl_exec($curl),true);
                    if(count($response) > 0)
                    {
                        if($response['status'] == 400 || $response['status'] == 500)
                        {
                            $finalresponse = array(
                                'status' => 'error',
                                'message' => $response['error']['message']
                            );
                            echo json_encode($finalresponse);           
                        }else{
                            if($response['data']['code'] == 1018)
                            {
                                $finalresponse = array(
                                    'status' => 'success',
                                    'gender' => ucFirst(strtolower($response['data']['aadhaar_data']['gender'])),
                                    'state' => ucFirst($response['data']['aadhaar_data']['state'])
                                );
                                echo json_encode($finalresponse);              
                            }else{
                                $finalresponse = array(
                                    'status' => 'success',
                                    'gender' => ucFirst(strtolower($response['data']['aadhaar_data']['gender'])),
                                    'state' => ucFirst($response['data']['aadhaar_data']['state'])
                                );
                                echo json_encode($finalresponse);           
                            }
                        }
                    }else{
                        $finalresponse = array(
                            'status' => 'invalid',
                            'message' => 'Invalid Aadhar Number'
                        );
                        echo json_encode($finalresponse);           
                    }
                }else{
                    $finalresponse = array(
                        'status' => 'invalid',
                        'message' => 'Please enter 12 digit aadhar number'
                    );
                    echo json_encode($finalresponse);           
                }
                break;
            case 'PAN':
                if(strlen($_POST['idNumber']) == '10')
                {
                    $curl = curl_init();
                    $postdata = json_encode(array(
                        'pan_number' => $_POST['idNumber'],
                        'consent' => 'Y'
                    ));
                    $curl = curl_init();
