            {
                if($_POST['index'] == 2)
                {
                    $updatedata = array(
                        'well2' => $_POST['mobile'],
                        'isProfileContact2' => 0,
                    );   
                }else if($_POST['index'] == 3)
                {
                    $updatedata = array(
                        'well3' => $_POST['mobile'],
                        'isProfileContact3' => 0,
                    );   
                }
                else{
                    $updatedata = array(
                        'well1' => $_POST['mobile'],
                        'isProfileContact1' => 0,
                    );
