        if($getMaxId[0]['Id'] != NULL)
        {
            $id = $getMaxId[0]['Id'] + 1;
        }
        $Appli_no='FUNKH'.sprintf("%03d", $id);
        $getIfAlready = $this->db->query('select * from funnel_missing where Application_no="'.$Appli_no.'"')->result_array();
