            echo json_encode([
                'status' => 'error',
                'msg'    => isset($sendStatus['message']) ? $sendStatus['message'] : 'Failed to send OTP. Please try again later.'
            ]);
            die();
        }
        $update = $this->db->query('update digilockerUser set otp="'.$otp.'" where Id='.$getUserDetail[0]['Id']);
