            }else{
                $page_data['data'] = $this->db->query('SELECT * FROM funnel_missing WHERE userId='.$_SESSION['user_id'].' and Application_no = "'.$_GET['appNo'].'" OR (Application_no IS NULL AND CONCAT("MAH", LPAD(id, 3, "0")) = "'.$_GET['appNo'].'")')->result_array();
