            {
                $mobileNo = $getApplicationDetails[0]['well1'];
            }else{
                if($getApplicationDetails[0]['well2'] != NULL)
                {
                    $mobileNo = $getApplicationDetails[0]['well2'];
                }else{
                    $mobileNo = $getApplicationDetails[0]['well3'];
                }
            }
            $aadharNumber = '';
            $thanaName = '';
            $thanaUsername = '';
            $username = '';
