                $username = $getUserDetail[0]['aadharname'];
            }else if($getApplicationDetails[0]['policeId'] != NULL)
            {
                $getUserDetail = $this->db->query('select * from police where police_id='.$getApplicationDetails[0]['policeId'])->result_array();
